rule.method:
{
							AcctBsSgmt bs = record.getAcctBsSgmt();
							List repymtInfs = record.getRltRepymtInfSgmt().getRltRepymtInf();
							if (repymtInfs != null && !repymtInfs.isEmpty()) {
								for (int i = 0; i < repymtInfs.size(); i++) {
									RltRepymtInf rltRepymtInf = (RltRepymtInf) repymtInfs.get(i);
									if("2".equals(rltRepymtInf.getArlpIDType())){
										if (bs.getIDNum().equals(rltRepymtInf.getArlpCertNum())
												&& bs.getIDType().equals(rltRepymtInf.getArlpCertType())) {
											return false;
										}
									}
								}
							}
							return true;
						}
